   private static HuffmanWaveletTree[] trees;

   @BeforeClass
   public static void setUpBeforeClass() throws Exception {
      trees = new HuffmanWaveletTree[sequences.length];
      byte[] sequence;
      for (int i = 0; i < sequences.length; i++) {
         sequence = Arrays.copyOf(sequences[i], sequences[i].length);
         final byte[] characters = determinCharacters(alphabet[i]);
         final int[] frequencies = countFrequencies(characters, sequence);
         trees[i] = new HuffmanWaveletTree(sequence, characters, frequencies);
      }
   }

   @AfterClass
   public static void tearDownAfterClass() throws Exception {
   }

   @Test
   public void testGetCharacter() {
      for(int i = 0; i < sequences.length; i++) {
         for(int j = 0; j < sequences[i].length; j++) {
//            if (i == 0 && j == 7 ) {
//               System.out.println("");
//            }
            assertEquals(String.format("Error for i=%d, j=%d", i,j), sequences[i][j], trees[i].getCharacter(j));
         }
      }
   }

   @Test
   public void testRank() {
      for(int i = 0; i < sequences.length; i++) {
         for(int j = 1; j <= sequences[i].length; j++) {
            for (byte b = alphabet[i][0]; b <= alphabet[i][1]; b++ ) {
//               if (i ==14 && j == 1 && b == 0) {
//                  System.out.println("");
//               }
               
               
               int rank = rank(b, j, sequences[i]);
               assertEquals(String.format("Error for i=%d, j=%d, b=%d", i,j,b), rank, trees[i].rank(b, j));
            }
         }
      }
   }
   
   @Test(expected=IndexOutOfBoundsException.class)
   public void testEmpty() {
      HuffmanWaveletTree hwt = new HuffmanWaveletTree(new byte[]{}, new byte[]{}, new int[]{});
      assertEquals(0, hwt.rank((byte)1, 1));
      hwt.getCharacter(1);
   }

   /**
    * Returns the number of times the given character appears in the prefix of the sequence 'sequence[0,...,prefix-1]';
    * @param character
    *           The character to count.
    * @param prefix
    *           Length of the prefix of the sequence within the character is counted. 
    * @param sequence
    *           The sequence.
    * @return Number of times the given character appears within the positions 0 (inclusive) to prefix (exclusive) of this BitArray.
    */
   private static int rank(byte character, int prefix, byte[] sequence) {
      int counter = 0;
      for(int i = 0; i < prefix && i < sequence.length; i++) {
         if (sequence[i] == character) {
            counter++;
         }
      }
      return counter;
   }
   
   private static byte[] determinCharacters(byte[] alphabet) {
      byte[] characteres = new byte[alphabet[1]-alphabet[0]+1];
      for(int i = 0; i < characteres.length; i++) {
         characteres[i] = (byte)(alphabet[0]+i);
      }
      assert characteres[characteres.length-1] == alphabet[1];
      return characteres;
   }
   
   private static int[] countFrequencies(byte[] characteres, byte[] sequence) {
      int[] frequencies = new int[characteres.length];
      
      for(int i = 0; i < sequence.length; i++) {
         for(int j = 0; j < characteres.length; j++) {
            if (characteres[j] == sequence[i]) {
               frequencies[j]++;
               break;
            }
         }
      }
      
      return frequencies;
   }

